        public static TextModel GetTextForframe(int frameNumber)
        {
            return TextsForFrames.Find(x => x.FrameNumber == frameNumber);
        }

        public static bool IfTemplateExists(int frameNumber)
        {
            var result = TextsForFrames.Find(x => x.FrameNumber == frameNumber);

            if (result == null)
            {
                return false;
            }
            else
            {
                return true;
            }
        }
